import { useEffect, useRef, useState } from "react";

export function useToggle(initial){
    let loc = localStorage.getItem("data")

    const[val , setval] =  useState([loc[0].join().slice(",")])
    const ref = useRef()

    const gum = function(){
        setval(ref.current.value)
        setval([...val,ref.current.value])



    }

    useEffect(function(){

        localStorage.setItem("data",[[...val]])


        console.log(val)

    },[val])

    const handleLocalClear = function(){
        localStorage.clear()
        setval(val.splice())
    }

    return[
        val,
        gum,
        ref,
        handleLocalClear
    ]
}
