import { useEffect, useRef, useState } from "react";

export function useToggle(initial){
    let loc = localStorage.getItem("data")

    const[val , setval] =  useState([])
    const  [chn,setchn] = useState(localStorage.getItem("data"))
    const ref = useRef()

    const gum = function(){
        setval(ref.current.value)
        setval([...val,ref.current.value])  



    }

    useEffect(function(){

        localStorage.setItem("data",[[...val]])

        setchn(chn.slice(""))
        console.log(val)
        console.log(chn)
    },[val])

    const handleLocalClear = function(){
        localStorage.clear()
        setval(val.splice())
    }

    return[
        val,
        gum,
        ref,
        handleLocalClear
    ]
}
