import logo from './logo.svg';
import './App.css';
import { useState } from 'react';

function App() {
  const [click,setclick] = useState(0)

  const open = function(e){
    if(click<1){
      e.stopPropagation()
      setclick(click+1)
    }
  }

  const close = function(e){
    if(click>0){
    e.stopPropagation()
    setclick(click-1)
  }
  }
  console.log(click)
  return (
    <div className={click==1?"App":"App1"} onClick={close}>
      <button onClick={open}>Modal</button>
      <div className={click==1?"modalwindowopen":"modalwindowsclose"}></div>
    </div>
  );
}

export default App;
